import axios from "axios"
import { createReducer, createAsyncThunk } from "@reduxjs/toolkit"

export const sendLoginRequest = createAsyncThunk("LOGIN", ({userEmail, userPass}) => {
  return axios.post("/api/login", {
    email: userEmail.target.value,
    password: userPass.target.value
  })
})

export const registerUser = createAsyncThunk("REGISTER", ({userEmail, userPass}) => {
  return axios.post("/api/register", {
    email: userEmail.target.value,
    password: userPass.target.value
  })
})

export const getFavorites = createAsyncThunk("GET_FAVORITES", () => {
  // console.log("MOVIE NAME: ",movieName.target.value)
  return axios.get(`/api/favorites`).then((res) => res.data)
  //get: api/movies
})

export const addToFavorites = createAsyncThunk(
  "ADD_TO_FAVORITES",
  (movieId, thunkAPI) => {
    const { user } = thunkAPI.getState();
    if (!user.id) throw new Error("You need to be logged in")
    return axios
      .put(`/api/favorites?userId=${user.id}&movieId=${movieId}`)
      .then((res) => res.data)
  }
)

export const removeFromFavorites = createAsyncThunk(
  "REMOVE_FROM_FAVORITES",
  (movieId, thunkAPI) => {
    const { user } = thunkAPI.getState();
    if (!user.id) throw new Error("You need to be logged in")
    return axios
      .delete(`/api/favorites?userId=${user.id}&movieId=${movieId}`)
      .then((res) => res.data)
  }
)

const userReducer = createReducer([], {
  [sendLoginRequest.fulfilled]: (state, action) => action.payload,
  [registerUser.fulfilled]: (state, action) => action.payload,
  [addToFavorites.fulfilled]: (state, action) => action.payload,
  [removeFromFavorites.fulfilled]: (state, action) => action.payload,
});

export default userReducer;
