import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from 'react-router-dom'
import MovieBlock from './MovieBlock';
import { getFavorites } from '../store/user'

export default function Favorites() {
    //hacer un axios para buscar el array de favs a la DB
   const favorites = getFavorites()
  return (
      <div className='movie-list'> 
      <Link to='/' style={{ textDecoration: 'none' , paddingLeft: '20px'}}>
            <h3 className='goback'>Go Back</h3>
        </Link>
        {favorites.Search ?
        favorites.Search.map(movie => (
          <div key={movie.imdbID}>  
          <Link  to={`/movies/${movie.Title}`} style={{ textDecoration: 'none' }}>
            <MovieBlock
              className='movie-block'
              title={movie.Title}
              posterUrl={movie.Poster}
              />
          </Link>
          </div>
        )) 
        : <p>No favorites found.</p>
      }
      </div>
  )
}
