import * as React from "react";
import { useDispatch } from "react-redux";
import { sendLoginRequest } from '../store/user'
import { useHistory } from "react-router-dom";

export default function Register() {

  const dispatch = useDispatch()
  const [userEmail, setUserEmail] = React.useState('')
  const [userPass, setUserPass] = React.useState('')
  const [isLoggedIn, setIsLoggedIn] = React.useState(false)

  const history = useHistory()

  const handleClick = (e) => {
    e.preventDefault()
    let userData = {
      userEmail, 
      userPass
    }
    dispatch(sendLoginRequest(userData))
      .then(() => {
        setIsLoggedIn(true)
        history.push('/favorites')
      })
  }

  return (
    <div className='register'>
      <h2>Login</h2>
      <form>
        <input name="email" placeholder='Email' onChange={data => setUserEmail(data)} />
        <input name="password" type='password' placeholder='Password'onChange={data => setUserPass(data)} />
        <button type="submit" onClick={handleClick}>Login</button>
      </form>
  </div>
  );
}
