import React from "react"
import { useSelector } from 'react-redux';

export default function SinlgeMovie(props){
    // console.log("PROPS SINGLE: ", props)

    const movies = useSelector(store => store.movies)


    const selectedMovie = movies.Search.find((movie) => {
        if(movie.Title === props.moviename.name)
        console.log("MOVIE: ", movie)
        return movie
    })

    return (
        <div className="movie-block">
            <div>
                <div>
                    <h3 className="movie-title">{selectedMovie.Title}</h3>
                </div>
                <div>
                    <img src={selectedMovie.Poster} alt="MoviePoster" />
                </div> 

            </div>
        </div>
    )
}
